mapper.select:
select idx, id, local1, local2, subway_nm, level, storage_yn, storage, distance, description, rcmnd_reason, img_url, del_yn, create_dt, update_dt
from course
where 1=1
           AND idx = #{idx}              
            AND id = #{id}                
      AND subway_nm = #{subwayNm}   
         AND level = #{level}          
     AND storage_yn = #{storageYn} 
         AND del_yn = #{delYn}         
      AND create_dt = #{createDt}

select count(*)
from course
where 1=1
           AND idx = #{idx}              
            AND id = #{id}                
      AND subway_nm = #{subwayNm}   
         AND level = #{level}          
     AND storage_yn = #{storageYn} 
         AND del_yn = #{delYn}         
      AND create_dt = #{createDt}
